import sqlite3
con=sqlite3.connect("users.db")
def insertData(NAME,aadharno,parentsname,phoneno,clas,section):
    qry="insert into users(NAME,aadharno,parentsname,phoneno,clas,section) values (?,?,?,?,?,?);"
    con.execute(qry,(NAME,aadharno,parentsname,phoneno,clas,section))
    con.commit()
    print(" User Details  added Sucessfully")
    
def updateData(NAME,aadharno,parentsname,phoneno,clas,section,ID):
    qry="update users set NAME=?,aadharno=?,parentsname=?,phoneno=?,clas=?,section=? where ID =?;"
    con.execute(qry,(NAME,aadharno,parentsname,phoneno,clas,section,ID))
    con.commit()
    print(" User Details  updated Sucessfully")   
    
def deleteData(ID):
    qry="delete from users where ID =?;"
    con.execute(qry,(ID))
    con.commit()
    print(" User Details  Deleted Sucessfully")    
    
def selectData():
    qry="select * from users ;"
    result=con.execute(qry)
    for row in result:
        print(row)    
print("""
1.Insert
2.Update
3.delete
4.Select
""")

