import sqlite3
con=sqlite3.connect("fees.db")
def insertData(NAME,clas,stream,fees):
    qry="insert into fees(NAME,clas,stream,fees) values (?,?,?,?);"
    con.execute(qry,(NAME,clas,stream,fees))
    con.commit()
    print(" User Fees Details  added Sucessfully")
    
def updateData(NAME,clas,stream,fees,ID):
    qry="update fees set NAME=?,clas=?,stream=? fees=? where ID =?;"
    con.execute(qry,(NAME,clas,stream,fees,ID))
    con.commit()
    print(" User Fees Details  updated Sucessfully")   
    
def deleteData(ID):
    qry="delete from fees where ID =?;"
    con.execute(qry,(ID))
    con.commit()
    print(" User Fees Details  Deleted Sucessfully")    
    
def selectData():
    qry="select  Name,class,stream from fees where ID=?;"
    result=con.execute(qry,(ID))
    con.commit()
    for row in result:
        print(row) 

