import sqlite3
con = sqlite3.connect("marks.db")
def insertData(NAME,language1,language2,maths,science,social,total,average,Remarks):
    qry="insert into marks(NAME,language1,language2,maths,science,social,total,average,Remarks) values (?,?,?,?,?,?,?,?,?);"
    con.execute(qry,(NAME,int(language1),int(language2),int(maths),int(science),int(social),int(total),int(average),str(Remarks)))
    con.commit()
    print(" User Details  added Sucessfully")
    
    
def updateData(NAME,language1,language2,maths,science,social,total,average,Remarks,ID):
    qry="update marks set NAME=?,language1=?,language2=?,maths=?,science=?,social=? ,total=? ,average=?, Remarks=?  where ID =?;"
    con.execute(qry,(NAME,int(language1),int(language2),int(maths),int(science),int(social),int(total),int(average),str(Remarks)),ID)
    con.commit()
    print(" User Details  updated Sucessfully")   
    
def deleteData(ID):
    qry="delete from marks where ID =?;"
    con.execute(qry,(ID))
    con.commit()
    print(" User Details  Deleted Sucessfully")   
    

def selectData(ID):
    qry="select name,language1,language2,maths,science,social from marks where ID=?;"
    result=con.execute(qry,(ID))
    con.commit()
    for row in result:
        print(row) 
